lickr</small>', 'Open Web Fellows', '', 'inherit', 'closed', 'closed', '', '6-revision-v1', '', '', '2014-07-24 14:01:22', '2014-07-24 21:01:22', '', 6, 'http://localhost/mozilla/2014/07/24/6-revision-v1/', 0, 'revision', '', 0),
